mapper.select:
SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN member AS M ON A.memberId = M.id WHERE displayStatus = 1 AND boardId = #{boardId}
AND title LIKE CONCAT('%',#{searchKeyword},'%')
AND sortId = #{sortId}
ORDER BY id DESC

SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN member AS M ON A.memberId = M.id WHERE A.displayStatus = 1 AND A.boardId = #{id}
AND A.title LIKE CONCAT('%',#{searchKeyword},'%')
AND sortId = #{sortId}
ORDER BY A.id DESC LIMIT #{limitFrom}, #{itemsInAPage}

mapper.update:
UPDATE article
delDate=NOW(), delStatus=1, displayStatus=0,
WHERE id=#{id}

UPDATE article
updateDate=NOW(), title=#{title}, body=#{body},
WHERE id=#{id}

UPDATE article

	hit = hit + 1,

WHERE id=#{id}
