mapper.select:
SELECT COUNT(*) AS cnt
FROM member
WHERE loginId = #{loginId}

mapper.update:
UPDATE member SET
updateDate = NOW(),
loginPw = #{loginPwReal}
WHERE id = #{id}

UPDATE member
updateDate = NOW(), delStatus = 1, delDate = NOW(),
WHERE id = #{id}

UPDATE member
SET partyId = #{newPartyId}
WHERE id = #{id}
